import {
  View,
  Panel,
  Group,
  PanelHeader,
  PanelHeaderBack,
  CellButton,
  PanelHeaderContent,
} from "@vkontakte/vkui";
import { Views } from "../../../model/views";
import ChannelView from "./ChannelView";
import { Icon16MoreVertical, Icon24Add, Icon24LinkCircle } from "@vkontakte/icons";
import AlterActionSheet from "./AlterActionSheet";
import { renameChannel } from "../../../serverApi/serverApi";
import React from "react";

export default function Channels({ returnBack, roomInfo }) {
  return roomInfo == null ? (
    <></>
  ) : (
    <View id={Views.Rooms.id} activePanel={Views.Rooms.panels.list.id}>
      <Panel id={Views.Rooms.panels.list.id}>
        <PanelHeader before={<PanelHeaderBack onClick={returnBack} />}>
          <>
            {roomInfo.name}
            <AlterActionSheet roomInfo={roomInfo} returnBack={returnBack} />
          </>
        </PanelHeader>
        <Group>
          {roomInfo.channels && roomInfo.channels.length > 0 && (
            roomInfo.channels.map((channel) => (
              <ChannelView
                channel={channel}
                connectToChannel={() =>
                  console.log("stub connect to channel ", channel.id)
                }
                renameToChannel={renameChannel}
              />
            ))
          )}
          <CellButton onClick={() =>  {console.log("channel create")}} centered before={<Icon24Add />}>
            Создать канал
          </CellButton>
        </Group>
      </Panel>
    </View>
  );
}
