import { Views } from "../model/views";
import { Root } from "@vkontakte/vkui";
import Rooms from "./views/rooms/Rooms";
import Channels from "./views/Channels/Channels";
import { useEffect, useState } from "react";
import { getVkUserId, joinRoomOnStart } from "../bridge/bridgeLib";


async function isAdminInRoom (room, user_id) {
  getVkUserId()
  console.log("room", room, "  ", user_id)
  if (room) {
      let findMember = room.members.filter(member => member.id === user_id)
      console.log("is admin", findMember)
      return findMember.role === "admin"
  }
}

export default function Main() {
  const [activeView, setActiveView] = useState(Views.Servers.id);
  const [activeRoomInfo, setRoomInfo] = useState(null);



  const enterRoom = function (roomInfo) {
    setRoomInfo(roomInfo);
    setActiveView(Views.Rooms.id);
  };

  useEffect(() => {
    if (joinRoomOnStart !== undefined)
      enterRoom(joinRoomOnStart)
  }, []);

  return (
    <Root activeView={activeView}>
      <Rooms id={Views.Servers.id} enterRoom={enterRoom} userIsAdmin={isAdminInRoom(activeRoomInfo)}/>
      <Channels
        id={Views.Rooms.id}
        roomInfo={activeRoomInfo}
        returnBack={() => setActiveView(Views.Servers.id)}
      />
    </Root>
  );
}
