import { createContext, useState } from "react";
import { Alert } from "@vkontakte/vkui";
import { Locales } from "../../locales/locales";
import React from 'react'
export const AlertContext = createContext();
export const AlertManagerContext = createContext();

export default function AlertProvider({ children }) {
  const [alert, setAlert] = useState(null);

  const alertManager = {
    showAlert: (alertToShow) => setAlert(alertToShow),
    closeAlert: () => setAlert(null),
  };

  return (
    <AlertContext.Provider value={alert}>
      <AlertManagerContext.Provider value={alertManager}>
        {children}
      </AlertManagerContext.Provider>
    </AlertContext.Provider>
  );
}

export function ErrorAlert({ message, alertManager }) {
  return (
    <Alert
    header={Locales.Alerts.ErrorTitle}
      text={message}
      onClose={alertManager.closeAlert}
    />
  );
}

export function DeleteErrorAlert({ alertManager, deleteRoom }) {
  return (
    <Alert
      header={Locales.Alerts.ConfirmDeleteRoomTitle}
      actions={[
        {
          title: Locales.Alerts.Delete,
          mode: "destructive",
          autoClose: true,
          action: () => {deleteRoom()}
        },
        {
          title: Locales.Alerts.Cancel,
          mode: "cancel",
          autoClose: true,
        }
      ]}
      onClose={alertManager.closeAlert}
    />
  );
}
