import React, { useContext, useState } from "react";
import {
  Icon16MoreVertical,
  Icon20DeleteOutline,
  Icon20ShareOutline,
  Icon20WriteOutline,
  Icon28DeleteOutline,
  Icon28EditOutline,
  Icon28ShareOutline,
} from "@vkontakte/icons";

import { ActionSheet, ActionSheetItem } from "@vkontakte/vkui";
import {
  copyToClipboard,
  createInviteURL,
  getFromStorage,
  perfomAppText,
  removeFromStorage,
} from "../../../bridge/bridgeLib";
import { Locales } from "../../../locales/locales";
import { VkStorageKeys } from "../../../bridge/storageKeys";
import { deleteRoom } from "../../../serverApi/serverApi";
import { AlertManagerContext, DeleteErrorAlert } from "../../contexts/AlertProvider";

function AlterActionSheet({ targetRef, roomInfo, returnBack }) {
  const [actionSheet, setActionSheet] = useState(null);
  const onClose = () => setActionSheet(null);
  const alertManager = useContext(AlertManagerContext)
  const iconsTargetRef = React.useRef();

  const handleEditRoom = () => {
    console.log("edit");
  };

  async function handleShareRoom() {
    var inviteURL = await createInviteURL(roomInfo.id);
    copyToClipboard(inviteURL);
  }

  const handleDeleteRoom = () => {
      alertManager.showAlert(DeleteErrorAlert({alertManager: alertManager,  deleteRoom: () => {deleteRoom(roomInfo.id); returnBack()}}))
  };

  const openActionSheet = () => {
    setActionSheet(
      <ActionSheet
        onClose={onClose}
        placement="center"
        toggleRef={iconsTargetRef}
      >
        <ActionSheetItem
          onClick={handleEditRoom}
          autoClose
          before={<Icon28EditOutline />}
        >
          {Locales.ActionSheetName.EditRoom}
        </ActionSheetItem>

        <ActionSheetItem
          onClick={handleShareRoom}
          autoClose
          before={<Icon28ShareOutline />}
        >
          {Locales.ActionSheetName.ShareRoom}
        </ActionSheetItem>

        <ActionSheetItem
          onClick={handleDeleteRoom}
          autoClose
          before={<Icon28DeleteOutline />}
          mode="destructive"
        >
          {Locales.ActionSheetName.DeleteRoom}
        </ActionSheetItem>
      </ActionSheet>
    );
  };

  return (
    <>
      <Icon16MoreVertical getRootRef={iconsTargetRef} onClick={openActionSheet} />
      {actionSheet}
    </>
  );
}

export default AlterActionSheet;
