import {
  Icon16DeleteOutline,
  Icon16PenOutline,
  Icon24RefreshOutline,
  Icon28VolumeOutline,
} from "@vkontakte/icons";
import { IconButton, SimpleCell, Tooltip } from "@vkontakte/vkui";
import { joinCall } from "../../../bridge/bridgeLib";
import { Locales } from "../../../locales/locales";
import { useContext, useEffect, useState } from "react";
import { AlertManagerContext, TipAlert } from "../../contexts/AlertProvider";


export default function ChannelView({ channel, channelIndex, renameToChannel, editModeActive, deleteToChannel, refreshChannel, userIsAdmin }) {

  const [isTipShown, setTipShown] = useState(channelIndex == 0)

  const alerts = useContext(AlertManagerContext)


  const isShlyahChannel = channel.name === "шлях";

  // Стили для канала "шлях"


  useEffect(() => {
    let counter = localStorage.getItem("tipCounter")
    if (counter == null){
      localStorage.setItem("tipCounter", "1")
      return
    }
    let counterVal = JSON.parse(counter)
    if (counterVal > 3) {
      setTipShown(false)
    }
    localStorage.setItem("tipCounter", JSON.stringify(counterVal + 1))
  }, [])

  function joinChannel() {
    joinCall(channel.joinlink, () => {})
  }

  return (
    <SimpleCell
      style={isShlyahChannel ? shlyahStyle : {}}
      before={<Icon28VolumeOutline />}
      after={
        userIsAdmin &&
        <Tooltip
              isShown={isTipShown}
              text={Locales.Hints.RefreshChannel}
              onClick={e => {
                e.preventDefault()
                e.stopPropagation()
                e.nativeEvent.stopImmediatePropagation()
                setTipShown(false)
              }}
              onClose={() => setTipShown(false)}
            >
          <IconButton onClick={e => {
            e.preventDefault()
            e.stopPropagation()
            e.nativeEvent.stopImmediatePropagation()
            refreshChannel(channel.id)
          }}>

                <Icon24RefreshOutline />
          </IconButton>
        </Tooltip>
      }
      onClick={() => {
        if (!userIsAdmin){
          joinChannel()
          return
        }
        let isFirstCall = localStorage.getItem("isFirstCall")
        if (isFirstCall == null){
          localStorage.setItem("isFirstCall", "false")
          alerts.showAlert(TipAlert({message: Locales.Hints.DontKillCall, alertManager: alerts, onClose: joinChannel}))
          return
        }
        joinChannel()
      }}
    >
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <b>#{channel.name}</b>
        {editModeActive && <IconButton
          onClick={e => {
            e.preventDefault()
            e.stopPropagation()
            e.nativeEvent.stopImmediatePropagation()
            renameToChannel()
            }}>
          <Icon16PenOutline />
        </IconButton>}
        {editModeActive && <IconButton
          style={{ color: 'red' }}
          onClick={e => {
            e.preventDefault()
            e.stopPropagation()
            e.nativeEvent.stopImmediatePropagation()
            deleteToChannel(channel.id)
            }}>
          <Icon16DeleteOutline />
        </IconButton>}

      </div>

    </SimpleCell>
  );
}
