import React, { useContext, useState } from "react";
import {
  Icon16MoreVertical,
  Icon20DeleteOutline,
  Icon20ShareOutline,
  Icon20WriteOutline,
  Icon28DeleteOutline,
  Icon28DoorArrowRightOutline,
  Icon28EditOutline,
  Icon28ShareOutline,
} from "@vkontakte/icons";

import { ActionSheet, ActionSheetItem } from "@vkontakte/vkui";
import {
  copyToClipboard,
  createInviteURL,
  getFromStorage,
  perfomAppText,
  pushToStorage,
  removeFromStorage,
  shareInviteLink,
} from "../../../bridge/bridgeLib";
import { Locales } from "../../../locales/locales";
import { VkStorageKeys } from "../../../bridge/storageKeys";
import { deleteRoom } from "../../../serverApi/serverApi";
import {
  AlertManagerContext,
  DeleteErrorAlert,
} from "../../contexts/AlertProvider";

function AlterActionSheet({ targetRef, roomInfo, returnBack, handleActiveEditMode, userIsAdmin }) {
  const [actionSheet, setActionSheet] = useState(null);
  const onClose = () => setActionSheet(null);
  const alertManager = useContext(AlertManagerContext);
  const iconsTargetRef = React.useRef();



  async function handleExitRoom() {
    let idFromStorage = await getFromStorage(VkStorageKeys.JoinedRooms);
    let idToRemove = "," + roomInfo.id
    let updatedString = idFromStorage.replace(new RegExp(idToRemove, 'g'), "");
    pushToStorage(VkStorageKeys.JoinedRooms, updatedString)
    returnBack()
  }

  async function handleShareRoom() {
    await shareInviteLink(roomInfo.id)
  }

  const handleDeleteRoom = () => {
    alertManager.showAlert(
      DeleteErrorAlert({
        alertManager: alertManager,
        deleteRoom: () => {
          deleteRoom(roomInfo.id);
          returnBack();
        },
      })
    );
  };

  const openActionSheet = () => {
    console.log(userIsAdmin)
    setActionSheet(
      <ActionSheet
        onClose={onClose}
        placement="center"
        toggleRef={iconsTargetRef}
      >

        <ActionSheetItem
          onClick={handleShareRoom}
          autoClose
          before={<Icon28ShareOutline />}
        >
          {Locales.ActionSheetName.ShareRoom}

        </ActionSheetItem>
        {userIsAdmin && <ActionSheetItem
          onClick={handleActiveEditMode}
          autoClose
          before={<Icon28EditOutline />}
        >
          {Locales.ActionSheetName.EditRoom}
        </ActionSheetItem>}

        {userIsAdmin && <ActionSheetItem
          onClick={handleDeleteRoom}

          autoClose
          before={<Icon28DeleteOutline />}
          mode="destructive"
        >
          {Locales.ActionSheetName.DeleteRoom}
        </ActionSheetItem>}

         {userIsAdmin && <ActionSheetItem
          onClick={handleExitRoom}
          autoClose
          before={<Icon28DoorArrowRightOutline/>}>
              {Locales.ActionSheetName.ExitRoom}
          </ActionSheetItem>
  }

      </ActionSheet>
    );
  };

  return (
    <>
      <Icon16MoreVertical
        getRootRef={iconsTargetRef}
        onClick={openActionSheet}
      />
      {actionSheet}
    </>
  );
}

export default AlterActionSheet;
