import { editChannel } from '../../../../serverApi/serverApi';

export const channelOperation = (roomManager, alerts) => ({
  create: function (channelName) {
    const newRoom = roomManager.createChannel(channelName)
    editChannel(newRoom)
      .then((room) => {
        console.log("sucessful")
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },

  rename: function (newNameChannel, renameChannelId) {
    const updateRoom = renameChannel(newNameChannel, renameChannelId)
    editChannel(updateRoom)
      .then((room) => {
        redrawRoom()
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },

  delete: function (delChannenlId) {
    const updateRoom = deleteChannel(delChannenlId)
    editChannel(updateRoom)
      .then((room) => {
        redrawRoom()
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },

  refresh: function (channelId) {
    const updateRoom = refreshChannel(channelId)
    editChannel()
      .then((room) => {
        redrawRoom()
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },
});
