import {
  Icon16PenOutline,
  Icon24ArrowRightCircleOutline,
  Icon24PenOutline,
  Icon28VolumeOutline,
} from "@vkontakte/icons";
import { IconButton, SimpleCell } from "@vkontakte/vkui";
import { isAdminInRoom } from "../../../serverApi/serverApi";
import { getVkUserId } from "../../../bridge/bridgeLib";

export default function ChannelView({ channel, connectToChannel, renameToChannel, editModeActive}) { 
  return (
    <SimpleCell
      before={<Icon28VolumeOutline />}
      after={
        <IconButton onClick={connectToChannel}>
          <Icon24ArrowRightCircleOutline />
        </IconButton>
      }
      onClick={connectToChannel}
    >
      <div style={{display: 'flex', alignItems: 'center' }}> 
         <b>#{channel.name}</b>
      {editModeActive && <IconButton  onClick={renameToChannel}>
          <Icon16PenOutline/>
      </IconButton>}


      </div>

    </SimpleCell>
  );
}
