import { editChannel } from '../../../../serverApi/serverApi';
import { createChannel, renameChannel, deleteChannel, refreshChannel } from './channelManager';

export const channelOperation = (room, redrawRoom, alerts) => ({

  create: function (channelName) {
    const channelList = createChannel(room.channels, channelName)
    console.log(channelList)
    editChannel(channelList)
      .then((room) => {
        redrawRoom()
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },

  rename: function (newNameChannel, renameChannelId) {
    const channelList = renameChannel(room.channels, newNameChannel, renameChannelId)
    editChannel(channelList)
      .then((room) => {
        redrawRoom()
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },

  delete: function (delChannenlId) {
    const channelList = deleteChannel(room.channels, delChannenlId)
    editChannel(channelList)
      .then((room) => {
        redrawRoom()
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },

  refresh: function (channelId) {
    const channelList = refreshChannel(room.channels, channelId)
    editChannel()
      .then((room) => {
        redrawRoom()
      })
      .catch((error) => {
        console.log(error)
        alerts.showAlert(
          ErrorAlert({
            message: Locales.Errors.FailedCreateRoom,
            alertManager: alerts,
          })
        );
      });
  },
});
