import { Icon16Done, Icon28CheckCircleOutline } from "@vkontakte/icons";
import { Snackbar } from "@vkontakte/vkui";

 export const  openSuccess = (snackbar, setSnackbar) => {
    if (snackbar) return;
    setSnackbar(
    <Snackbar
        onClose={() => setSnackbar(null)}
        before={<Icon28CheckCircleOutline fill="var(--vkui--color_icon_positive)" />}
    >
        Звонок обновлен
    </Snackbar>,
    );
};

const openBaseWithAction = () => {
    if (snackbar) return;
    setSnackbar(
      <Snackbar
        onClose={() => setSnackbar(null)}
        before={<Icon24ThumbsUpOutline fill="var(--vkui--color_icon_accent)" />}
      >
        Ссылка скопирована
      </Snackbar>,
    );
  };
