import {
  View,
  Panel,
  Group,
  PanelHeader,
  PanelHeaderBack,
  CellButton,
  PanelHeaderContent,
  PullToRefresh,
  ScreenSpinner,
  Div,
  PanelHeaderButton,
  PanelHeaderContext,
  SimpleCell,
  SplitLayout,
  SplitCol,
  platform,
  Platform,
  usePlatform,
} from "@vkontakte/vkui";
import { ModalIds, Views } from "../../../model/views";
import ChannelView from "./ChannelView";
import {
  Icon16Dropdown,
  Icon16MoreVertical,
  Icon24Add,
  Icon24Done,
  Icon24LinkCircle,
  Icon28AddOutline,
  Icon28SettingsOutline,
  Icon28UsersOutline,
} from "@vkontakte/icons";
import AlterActionSheet from "./AlterActionSheet";
import { editChannel } from "../../../serverApi/serverApi";
import React, { useContext, useEffect, useState } from "react";
import { ModalManager } from "../../Modals";
import { Locales } from "../../../locales/locales";
import { getVkUserId } from "../../../bridge/bridgeLib";
import { AlertManagerContext, ErrorAlert } from "../../contexts/AlertProvider";

export default function Channels({ returnBack, roomInfo, redrawRoom }) {
  const [contextOpened, setContextOpened] = React.useState(true);
  const [editModeActive, setEditModeActive] = useState(false);
  const [userIsAdmin, setIsAdmin] = useState(false); 
  const alerts = useContext(AlertManagerContext)
  const [mode, setMode] = React.useState('all');
  const toggleContext = () => {
    setContextOpened((prev) => !prev);
  };
  const select = (e) => {
    const mode = e.currentTarget.dataset.mode;
    setMode(mode);
    requestAnimationFrame(toggleContext);
  };


  useEffect(() => {
    isAdminInRoom(roomInfo).then(x => setIsAdmin(x))
  }, []);

  const handleActiveEditMode = () => {
    setEditModeActive(!editModeActive);
  };

  async function isAdminInRoom(room) {
    let user_id = await getVkUserId(); 
    if (room) {
      let findMember = room.members.find(member => member.id === user_id); 
      return findMember && findMember.role === "admin"; 
    }
  }

  function renameChannel(channels, newNameChannel, channelId) {
    for (let channel of channels) {
      if (channel.id === channelId) {
        channel.name = newNameChannel
        roomInfo.channels = channels
        return roomInfo
      }
    }
  }

  function createChannel(channels, channelName) {
    if (channels.length === 0) {
      channels.push({
        id: 1,
        name: channelName
      });
    } else {
      let lastChannelId = channels[channels.length - 1].id;
      channels.push({
        id: lastChannelId + 1,
        name: channelName
      });
    }
    roomInfo.channels = channels
    return roomInfo
  }

  function deleteChannel(channels, channelId) {
    channels = channels.filter(channel => channel.id !== channelId)
    roomInfo.channels = channels
    return roomInfo
  }

  function refreshChannel(channels, channelId) {
    let channel = channels.find(x => x.id == channelId)
    if (channel) channel.joinlink = null
    return roomInfo
  }

  const createChannelInData = {
    editRoom: function (channelName) {
      editChannel(createChannel(roomInfo.channels, channelName))
        .then((room) => {
          redrawRoom()
        })
        .catch((error) => {
          console.log(error)
          alerts.showAlert(
            ErrorAlert({
              message: Locales.Errors.FailedCreateRoom,
              alertManager: alerts,
            })
          );
        });
    },
  };

  const renameChannelInData = {
    editRoom: function (channelName, channelId) {
      editChannel(renameChannel(roomInfo.channels, channelName, channelId))
        .then((room) => {
          redrawRoom()
        })
        .catch((error) => {
          console.log(error)
          alerts.showAlert(
            ErrorAlert({
              message: Locales.Errors.FailedCreateRoom,
              alertManager: alerts,
            })
          );
        });
    },
  };

  const DeleteChannelInData = {
    editRoom: function (channelId) {
      editChannel(deleteChannel(roomInfo.channels, channelId))
        .then((room) => {
          redrawRoom()
        })
        .catch((error) => {
          console.log(error)
          alerts.showAlert(
            ErrorAlert({
              message: Locales.Errors.FailedCreateRoom,
              alertManager: alerts,
            })
          );
        });
    },
  };


  const RefreshChannelData = {
    editRoom: function (channelId) {
      editChannel(refreshChannel(roomInfo.channels, channelId))
        .then((room) => {
          redrawRoom()
        })
        .catch((error) => {
          console.log(error)
          alerts.showAlert(
            ErrorAlert({
              message: Locales.Errors.FailedCreateRoom,
              alertManager: alerts,
            })
          );
        });
    },
  };

  const platform = usePlatform();
  const hasHeader = platform != Platform.VKCOM

  return roomInfo == null ? (
    <></>
  ) : (
    <SplitLayout
    style={{ justifyContent: 'center' }}
    header={hasHeader && <PanelHeader separator={false} />}
  >
    <SplitCol width="100%" maxWidth="560px" stretchedOnMobile autoSpaced>
    <View id={Views.Rooms.id} activePanel={Views.Rooms.panels.list.id}>
      <Panel id={Views.Rooms.panels.list.id}>

        <PanelHeader before={<PanelHeaderBack onClick={returnBack}/>}>
        <PanelHeaderContent
                aside={
                  <Icon16Dropdown
                    style={{
                      transform: `rotate(${contextOpened ? '180deg' : '0'})`,
                    }}
                  />
                }
                onClick={toggleContext}
              >
          </PanelHeaderContent>
          <PanelHeaderContext opened={contextOpened} onClose={toggleContext}>
              <SimpleCell
                before={<Icon28UsersOutline />}
                after={mode === 'all' ? <Icon24Done fill="var(--vkui--color_icon_accent)" /> : null}
                onClick={select}
                data-mode="all"
              >
                Communities
              </SimpleCell>
              <SimpleCell
                before={<Icon28SettingsOutline/>}
                after={
                  mode === 'managed' ? <Icon24Done fill="var(--vkui--color_icon_accent)" /> : null
                }
                onClick={select}
                data-mode="managed"
              >
                Managed Communities
              </SimpleCell>
            </PanelHeaderContext>
            <Group>
              <div style={{ height: 300 }} />
            </Group>

        {/* <Div style={{display: 'flex'}}>  
            <AlterActionSheet
              roomInfo={roomInfo}
              returnBack={returnBack}
              handleActiveEditMode={handleActiveEditMode}
              userIsAdmin={userIsAdmin} />
          </Div> */}
        </PanelHeader>
        <Group>
          {roomInfo.channels &&
            roomInfo.channels.length > 0 &&
            roomInfo.channels.map((channel, index) => (
              <ChannelView
                key={channel.id}
                channelIndex={index}
                channel={channel}
                renameToChannel={() => {
                  ModalManager.showModal(ModalIds.RenameChannel, {
                    renameChannelId: channel.id,
                    ...renameChannelInData,
                  })
                }}
                deleteToChannel={(id) => {DeleteChannelInData.editRoom(id)}}
                editModeActive={editModeActive}
                refreshChannel={(id) => RefreshChannelData.editRoom(id)}
                userIsAdmin={userIsAdmin}
              />
            ))}
            {userIsAdmin && 
          <CellButton
            onClick={() => {
              ModalManager.showModal(ModalIds.CreateChannel, createChannelInData);
            }}
            centered
            before={<Icon24Add />}
          >
            Создать канал
          </CellButton>
            }
        </Group>
      </Panel>
    </View>
    </SplitCol>
    </SplitLayout>
  );
};
