import React from 'react'
import {
  Icon16PenOutline,
  Icon24ArrowRightCircleOutline,
  Icon24PenOutline,
  Icon28VolumeOutline,
} from "@vkontakte/icons";
import { IconButton, SimpleCell } from "@vkontakte/vkui";

export default function ChannelView({ channel, connectToChannel, renameToChannel }) { 
  return (
    <SimpleCell
      before={<Icon28VolumeOutline />}
      after={
        <IconButton onClick={connectToChannel}>
          <Icon24ArrowRightCircleOutline />
        </IconButton>
      }
      onClick={connectToChannel}
    >
      <div style={{display: 'flex', alignItems: 'center' }}> 
         <b>#{channel.name}</b>
      <IconButton onClick={() => renameToChannel(room_info)}>
          <Icon16PenOutline/>
      </IconButton>
      </div>

    </SimpleCell>
  );
}
