export const roomManager = (room, setRoomInfo)
export const createChannel = (channels, channelName) => {
    const newChannel = {
      id: channels.length === 0 ? 1 : channels[channels.length - 1].id + 1,
      name: channelName
    };
    return [...channels, newChannel];
  };

  export const renameChannel = (channels, newNameChannel, channelId) => {
    return channels.map(channel => 
      channel.id === channelId ? { ...channel, name: newNameChannel } : channel
    );
  };

  export const deleteChannel = (channels, channelId) => {
    return channels.filter(channel => channel.id !== channelId);
  };

  export const refreshChannel = (channels, channelId) => {
    return channels.map(channel => 
      channel.id === channelId ? { ...channel, joinlink: null } : channel
    );
  };
